import { createSlice, PayloadAction } from "@reduxjs/toolkit";

interface CounterState {
  value: number;
}
interface PlayerState {
  play: boolean;
  shuffle: boolean;
  loop: boolean;
}

const initialState: CounterState = {
  value: 0,
};
const initialPlayerState: PlayerState = {
  play: false,
  shuffle: false,
  loop: false,
};

const musiclice = createSlice({
  name: "counter",
  initialState,
  reducers: {
    increment: (state) => {
      state.value += 1;
    },
    decrement: (state) => {
      state.value -= 1;
    },
    incrementByAmount: (state, action: PayloadAction<number>) => {
      state.value += action.payload;
    },
  },
});

export const { increment, decrement, incrementByAmount } = musiclice.actions;

export default musiclice.reducer;
