import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface CounterState {
  value: number;
}

interface PlayerState {
  play: boolean;
  shuffle: boolean;
  loop: boolean;
}

const initialCounterState: CounterState = {
  value: 0
};

const initialPlayerState: PlayerState = {
  play: false,
  shuffle: false,
  loop: false
};

const initialState: { counter: CounterState; player: PlayerState } = {
  counter: initialCounterState,
  player: initialPlayerState
};

const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    increment: state => {
      state.counter.value += 1;
    },
    decrement: state => {
      state.counter.value -= 1;
    },
    incrementByAmount: (state, action: PayloadAction<number>) => {
      state.counter.value += action.payload;
    },
    // Add reducers for player state
    setPlay: (state, action: PayloadAction<boolean>) => {
      state.player.play = action.payload;
    },
    setShuffle: (state, action: PayloadAction<boolean>) => {
      state.player.shuffle = action.payload;
    },
    setLoop: (state, action: PayloadAction<boolean>) => {
      state.player.loop = action.payload;
    }
  }
});

// Action creators are generated for each case reducer function
export const {
  increment,
  decrement,
  incrementByAmount,
  setPlay,
  setShuffle,
  setLoop
} = counterSlice.actions;

export default counterSlice.reducer;
